              "name": "buttonIcon",
              "required": "false",
              "type": "MenuIcon"
            },
            {
              "default": "-",
              "description": "点击按钮跳转的 url",
              "group": "basic",
              "name": "buttonUrl",
              "required": "false",
              "type": "string"
            },
            {
              "default": "-",
              "description": "是否使用原生 <a> 标签，通常用于外链的跳转",
              "group": "basic",
              "name": "buttonHref",
              "required": "false",
              "type": "string"
            },
