              "default": "`{}`",
              "description": "替代之前 detail 属性，用法相同。暂存的数据在事件传出时使用",
              "group": "basic",
              "name": "dataSource",
              "required": "false",
              "type": "any"
            },
            {
              "default": "`false`",
              "description": "是否禁用按钮",
              "group": "basic",
              "name": "disabled",
              "required": "false",
              "type": "boolean"
            },
            {
              "default": "`false`",
              "description": "按钮加载动画",
              "name": "loading",
              "required": "false",
              "type": "boolean"
            },
            {
              "default": "-",
              "description": "按钮类型  ButtonType(`\"link\" | \"default\" | \"primary\" | \"ghost\" | \"dashed\" | \"danger\" | \"icon\"|\"text\"`)",
              "group": "basic",
              "name": "buttonType",
              "required": "false",
              "type": "ButtonType"
            },
            {
              "default": "`false`",
              "description": "设置危险按钮",
              "group": "basic",
              "name": "buttonDanger",
              "required": "false",
              "type": "boolean"
            },
