              "group": "basic",
              "name": "buttonShape",
              "required": "false",
              "type": "\"circle\" | \"round\""
            },
            {
              "default": "-",
              "description": "按钮大小，支持大、小，不设置为默认中",
              "group": "basic",
              "name": "buttonSize",
              "required": "false",
              "type": "small | large"
            },
            {
              "default": "-",
              "description": "按钮的 tooltip",
              "group": "basic",
              "name": "tooltip",
              "required": "false",
              "type": "string"
            },
            {
              "default": "-",
              "description": "禁用按钮的 tooltip",
              "group": "basic",
              "name": "disabledTooltip",
              "required": "false",
              "type": "string"
            },
            {
              "default": "-",
